import React from 'react';
import {createRoot} from "react-dom/client"
import './index.css'

import io from 'socket.io-client'

//import logo from "./likeButton.jpg"
//import {MainContent} from "./MainContent.js"
const socket = io.connect('http://localhost:3001');

const container = document.getElementById('root')
const root= createRoot(container)

let userData={
  nick:"oski",
  room:null
}

while(true){
  let nick=prompt("nick: ")
  if(nick.length<=12){
    userData.nick=nick;
    break;
  }
}

while(true){
  let room=prompt("room: ")
  if(room.length==4 && !isNaN(room)){
    userData.room=room;
    break;
  }
}

socket.emit('users',userData.nick)

socket.on('users',data=>{
  console.log(data)
})

function BtnSend(){
  if(document.getElementById('mesage').value!=""){
    NewMessage(userData.nick, document.getElementById('mesage').value)
    document.getElementById('mesage').value=""
  }
}


function NewMessage(user,text){
  if(user==userData.nick){
    var newMessage=document.createElement('div')
    newMessage.className="myMessage"
    document.getElementById('ChatWindow').appendChild(newMessage)
    console.log(user,text)
  }
  else{
    var newMessage=document.createElement('div')
    newMessage.className="hisMessage"
    newMessage.innerHTML=user+"<br>"+text
  }

}


function NickBar(){
  return(
    <div id="NickBar"><h1>123456789012</h1></div>
  )
}
function ChatWindow(){
  return(
  <div id="ChatWindow"></div>
  )
}
function UserInput(){
  return(
  <div id="UserInput">
    <input type="text" id="mesage" placeholder="message"/><button id="send" onClick={BtnSend}>Send</button>
  </div>
    )
}
function Footer(){
  return(
  <div id="Footer">
  Just footer
  </div>
    )
}

const AllPage=(
  <div id="allPage">
    <NickBar />
    <ChatWindow />
    <UserInput />
    <Footer />
  </div>
);



root.render(AllPage)
